mapper.update:
update
editorCode_tb
set editor_name=#{editorName}, editor_code=#{code},
editor_path=#{path}
where editor_name=#{editorName}

mapper.select:
select editor_name editorName, editor_code code,
editor_path path from editorCode_tb
where editor_name=#{editorName}

select editor_name editorName, editor_code code,
editor_path path from editorCode_tb
where editor_name=#{path}
