mapper.update:
update
editorCode_tb
set id=#{id}, editor_name=#{editorName}, editor_startPage=#{startPage}, editor_code=#{code},
editor_path=#{path}
where editor_name=#{editorName}

mapper.select:
select id id, editor_name editorName, editor_startPage startPage, editor_code code,
editor_path path from editorCode_tb
where editor_path=#{path}
