mapper.update:
update
editorCode_tb
set editorCode_editorId=#{editorId}, editorCode_code=#{code}, editorCode_path=#{path}
where editorCode_path=#{path}

mapper.select:
select editorCode_editorId editorId, editorCode_code code, editorCode_path path 
from editorCode_tb
where editorCode_path=#{path}

select editorCode_editorId editorId, editorCode_code code, editorCode_path path
from editorCode_tb 
where editorCode_editorId=#{editorId}
