mapper.update:
update editorReview_tb
set editorReview_reviewId=#{reviewId}, editorReview_editorId=#{editorId}, editorReview_memberId=#{memberId}, editorReview_body=#{body}, editorReview_score=#{score}, editorReview writtenDate=#{writtenDate}
where editorReview_reviewId=#{reviewId}

mapper.select:
select editorReview_reviewId reviewId, editorReview_editorId editorId, editorReview_memberId memberId, editorReview_body body, editorReview_score score, editorReview writtenDate writtenDate
from editorReview_tb 
where editorReview_reviewId=#{reviewId}

select editorReview_reviewId reviewId, editorReview_editorId editorId, editorReview_memberId memberId, editorReview_body body, editorReview_score score, editorReview writtenDate writtenDate
from editorReview_tb 
where editorReview_editorId=#{editorId}
