mapper.update:
update memberContent_tb
set memberContent_memberId=#{memberId}, memberContent_documentId=#{documentId}, memberContent_memberPosition=#{memberPosition}
where memberContent_documentId=#{documentId}

mapper.select:
select memberContent_memberId memberId, memberContent_documentId documentId, memberContent_memberPosition memberPosition
from memberContent_tb 
where memberContent_memberId=#{memberId}

select memberContent_memberId memberId, memberContent_documentId documentId, memberContent_memberPosition memberPosition 
from memberContent_tb
where memberContent_memberId=#{memberId}
