mapper.update:
update temp_tb
set temp_memberId=#{memberId}, temp_contentsBody=#{contentsBody}, temp_date=#{date}
where temp_tempId=#{tempId}

mapper.select:
select temp_tempId tempId, temp_contentId contentId, temp_memberId memberId, temp_contentsBody contentsBody, temp_date date
from temp_tb 
where temp_tempId=#{tempId}

select temp_tempId tempId, temp_contentId contentId, temp_memberId memberId, temp_contentsBody contentsBody, temp_date date
from temp_tb 
where temp_tempId=#{tempId}
